mapper.insert:
 INSERT INTO pbo_4(p_no,name,id,subject,content,pwd) 
 VALUES((SELECT NVL(MAX(p_no)+1,1) FROM pbo_4),
#{name},#{id}, #{subject},#{content},#{pwd}
 )

mapper.update:
UPDATE pbo_4 SET
hit=hit+1
WHERE p_no=#{p_no}

mapper.select:
SELECT p_no,id,subject,content,TO_CHAR(regdate,'YYYY-MM-DD') as dbday,hit
FROM pbo_4
WHERE p_no=#{p_no}
