mapper.insert:
 INSERT INTO ppbo_4(pp_no,name,id,subject,content,pwd) 
 VALUES((SELECT NVL(MAX(pp_no)+1,1) FROM ppbo_4),
#{name},#{id}, #{subject},#{content},#{pwd}
 )

mapper.update:
UPDATE ppbo_4 SET
hit=hit+1
WHERE pp_no=#{pp_no}

mapper.select:
SELECT pp_no,id,subject,content,TO_CHAR(regdate,'YYYY-MM-DD') as dbday,hit
FROM ppbo_4
WHERE pp_no=#{pp_no}
