mapper.update:
UPDATE p_repl_4 SET
msg=#{msg}
WHERE pre_no=#{pre_no}

UPDATE p_repl_4 SET
msg='관리자가 삭제한 댓글입니다'
WHERE pre_no=#{pre_no}

UPDATE p_repl_4 SET
depth=depth-1
WHERE pre_no=#{pre_no}

mapper.select:
SELECT group_id,group_step,group_tab
FROM p_repl_4
WHERE pre_no=#{pre_no}

mapper.update:
UPDATE p_repl_4 SET
group_step=group_step+1
WHERE group_id=#{group_id} AND group_step> #{group_step}

UPDATE p_repl_4 SET
depth=depth+1
WHERE pre_no=#{pre_no}
