mapper.update:
UPDATE qbo_4 SET
hit=hit-1
WHERE q_no=#{q_no}

UPDATE qbo_4 SET
hit=hit+1
WHERE q_no=#{q_no}

mapper.select:
SELECT q_no,id,subject,content,hit,TO_CHAR(regdate,'YYYY-MM-DD') as dbday,group_id,group_step,pwd
FROM qbo_4
WHERE q_no=#{q_no}

mapper.update:
UPDATE qbo_4 SET
isreply=1
WHERE q_no=#{q_no}
